/******************************************************************** HANDLE POST REQUEST */
add_action('save_post', 'o_save_post');
function o_save_post($post_id) {
	// update meta
	if (isset($_POST['numbers'])) {
		$explode = explode(',',$_POST['numbers']);
		$map = array_map('trim',$explode); // prepare
		$_post_data = array(
			'numbers' =&gt; $map
		);
		update_post_meta($post_id, '_post_data', $_post_data);
	}
}
