add_filter('tiny_mce_before_init', 'my_tiny_mce_before_init');
function my_tiny_mce_before_init($initArray)
{
	$initArray['plugins'] = str_replace( array('wpfullscreen',',,') , array('', ',') , $initArray['plugins'] );
	return $initArray;
}
