<pre class="wiki">// Change imges urls in scrset
function re_calculate_image_srcset($sources, $size_array, $image_src, $image_meta, $attachment_id) {
	if ( eazyest_folderbase()-&gt;is_gallery_image( $attachment_id ) ) {
		$uploads_url = wp_upload_dir();
		$uploads_url = trailingslashit( $uploads_url['baseurl'] );
		foreach ($sources as $key =&gt; $value) {
			$sources[$key]['url'] = str_replace($uploads_url, eazyest_gallery()-&gt;address(), $value['url'] );
		}
	}
	return $sources;
}
add_filter('wp_calculate_image_srcset', 're_calculate_image_srcset', 100, 5);
